select division_order_id
        ,substr(custom_data, instr(custom_data, 'payerStatus=',1)+12,(instr(custom_data, 'return=',1,2) ) - (instr(custom_data, 'payerStatus=',1) + 13))payerStatus
        ,substr(custom_data, instr(custom_data, 'shipto.address1=',1)+16,(instr(custom_data, 'shipto.address2=',1) ) - (instr(custom_data, 'shipto.address1=',1) + 17))shiptoaddress1
        ,substr(custom_data, instr(custom_data, 'shipto.address2=',1)+16,(instr(custom_data, 'ship.tocity=',1) ) - (instr(custom_data, 'shipto.address2=',1) + 17))shiptoaddress2
        ,(case when custom_data like '%shipto.company%' then substr(custom_data, instr(custom_data, 'shipto.city=',1)+12,(instr(custom_data, 'shipto.company=',1) ) - (instr(custom_data, 'shipto.city=',1) + 13))
        else substr(custom_data, instr(custom_data, 'shipto.city=',1)+12,(instr(custom_data, 'shipto.countryID=',1) ) - (instr(custom_data, 'shipto.city=',1) + 13)) end)shiptocity
        ,(case when custom_data like '%shipto.name%' then substr(custom_data, instr(custom_data, 'shipto.countryID=',1)+17,(instr(custom_data, 'shipto.name=',1) ) - (instr(custom_data, 'shipto.countryID=',1) + 18))
        else substr(custom_data, instr(custom_data, 'shipto.countryID=',1)+17,(instr(custom_data, 'shipto.firstName=',1) ) - (instr(custom_data, 'shipto.countryID=',1) + 18)) end)shiptocountry
        ,(case when custom_data like '%shipto.name%' then substr(custom_data, instr(custom_data, 'shipto.name=',1)+12,(instr(custom_data, 'shipto.phone=',1) ) - (instr(custom_data, 'shipto.name=',1) + 13))
        else substr(custom_data, instr(custom_data, 'shipto.firstName=',1)+17,(instr(custom_data, 'shipto.lastName=',1) ) - (instr(custom_data, 'shipto.firstName=',1) + 18)) || ' ' || substr(custom_data, instr(custom_data, 'shipto.lastName=',1)+16,(instr(custom_data, 'shipto.phone=',1) ) - (instr(custom_data, 'shipto.lastName=',1) + 13)) end)shiptoname
        ,substr(custom_data, instr(custom_data, 'shipto.phone=',1)+13,(instr(custom_data, 'shipto.state=',1) ) - (instr(custom_data, 'shipto.phone=',1) + 14))shiptophone
        ,(case when custom_data like '%shipto.status=%' then substr(custom_data, instr(custom_data, 'shipto.state=',1)+13,(instr(custom_data, 'shipto.status=',1) ) - (instr(custom_data, 'shipto.state=',1) + 14))
        else substr(custom_data, instr(custom_data, 'shipto.state=',1)+13,(instr(custom_data, 'shipto.zip=',1) ) - (instr(custom_data, 'shipto.state=',1) + 14)) end)shiptostate
        ,(case when custom_data like '%shipto.status%' then substr(custom_data, instr(custom_data, 'shipto.status=',1)+14,(instr(custom_data, 'shipto.zip=',1) ) - (instr(custom_data, 'shipto.status=',1) + 15))
        else 'N/A' end)shiptostatus
        ,(case when custom_data like '%taxTotal%' then substr(custom_data, instr(custom_data, 'shipto.zip=',1)+11,(instr(custom_data, 'taxTotal=',1) ) - (instr(custom_data, 'shipto.zip=',1) + 12))
        else substr(custom_data, instr(custom_data, 'shipto.zip=',1)+11,length(custom_data) - (instr(custom_data, 'shipto.zip=',1) + 12)) end)shiptozip
        
