select distinct ol."/BIC/ZORGORDID" Order_ID
          ,(case when ac.GL_ACCOUNT in ('0007100100','0007100101') then 'Non-Fraud'
                    when ac.GL_ACCOUNT in ('0007100106','0007100105') then 'Fraud' else 'Other' end )SAP_GL_ACCT
          
          from SAPSR3."/BI0/AFIGL_O1400" ac,        --Accounting Segment
              SAPSR3."/BIC/AZSD_O0900" il,          --Invoice Lines
              SAPSR3."/BIC/AZSD_O0100" ol         --Order Lines
              
              where ac.FISCPER = '2016001'
               AND ac.bill_num = il.bill_num (+)
